get_ipython().run_cell_magic('html', '', '\n<canvas id="gameCanvas" width="800" height="600" style="border: 1px solid #fff;"></canvas>\n\n<script>\n    const canvas = document.getElementById(\'gameCanvas\');\n    const context = canvas.getContext(\'2d\');\n\n    const spaceship = { width: 50, height: 50, x: canvas.width / 2 - 25, y: canvas.height - 100 };\n    let asteroids = [];\n    let score = 0;\n    let gameOver = false;\n\n    function getRandomInt(min, max) {\n        return Math.floor(Math.random() * (max - min + 1)) + min;\n    }\n\n    function createAsteroid() {\n        const size = getRandomInt(20, 40);\n        const speed = getRandomInt(1, 3);\n        const x = getRandomInt(0, canvas.width - size);\n        const y = -size;\n        asteroids.push({ x: x, y: y, size: size, speed: speed });\n    }\n\n    function drawSpaceship() {\n        context.fillStyle = \'#0f0\';\n        context.fillRect(spaceship.x, spaceship.y, spaceship.width, spaceship.height);\n    }\n\n    function drawAsteroids() {\n        context.fillStyle = \'#f00\';\n        for (const asteroid of asteroids) {\n            context.fillRect(asteroid.x, asteroid.y, asteroid.size, asteroid.size);\n        }\n    }\n\n    function drawScore() {\n        context.fillStyle = \'#fff\';\n        context.font = \'24px Arial\';\n        context.fillText(`Score: ${score}`, 10, 30);\n    }\n\n    function drawGameOver() {\n        context.fillStyle = \'#fff\';\n        context.font = \'36px Arial\';\n        context.fillText(\'Game Over\', canvas.width / 2 - 100, canvas.height / 2);\n    }\n\n    function draw() {\n        context.clearRect(0, 0, canvas.width, canvas.height);\n        drawSpaceship();\n        drawAsteroids();\n        drawScore();\n\n        if (gameOver) {\n            drawGameOver();\n        }\n    }\n\n    function update() {\n        if (gameOver) {\n            return;\n        }\n\n        // Update asteroids position and check for collisions\n        for (let i = 0; i < asteroids.length; i++) {\n            asteroids[i].y += asteroids[i].speed;\n\n            // Check collision with spaceship\n            if (spaceship.x < asteroids[i].x + asteroids[i].size &&\n                spaceship.x + spaceship.width > asteroids[i].x &&\n                spaceship.y < asteroids[i].y + asteroids[i].size &&\n                spaceship.y + spaceship.height > asteroids[i].y) {\n                gameOver = true;\n            }\n\n            // Remove asteroids that move off screen\n            if (asteroids[i].y > canvas.height) {\n                asteroids.splice(i, 1);\n                i--;\n                score++;\n            }\n        }\n\n        // Add new asteroids periodically\n        if (Math.random() < 0.02) {\n            createAsteroid();\n        }\n\n        draw();\n        requestAnimationFrame(update);\n    }\n\n    // Start the game loop\n    update();\n\n    // Handle spaceship movement with mouse\n    canvas.addEventListener(\'mousemove\', function(event) {\n        const rect = canvas.getBoundingClientRect();\n        spaceship.x = event.clientX - rect.left - spaceship.width / 2;\n    });\n</script>\n')

